import React, { useEffect, useRef, useState } from 'react';

export default function AIAgentPage() {
  const [textIndex, setTextIndex] = useState(0);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [showButtons, setShowButtons] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const text = '你好，我是您的AI找工作助手，请问能帮您什么吗？';
  const fullTextRef = useRef('');
  const intervalRef = useRef(null);

  const speak = (sentence) => {
    const synth = window.speechSynthesis;
    const voices = synth.getVoices();

    // 优先选女性中文
    const selectedVoice =
      voices.find((v) => v.lang.startsWith('zh') && v.name.includes('女')) ||
      voices.find((v) => v.lang.startsWith('zh')) ||
      null;

    const utterance = new SpeechSynthesisUtterance(sentence);
    utterance.lang = 'zh-CN';
    utterance.voice = selectedVoice;

    utterance.onstart = () => {
      setIsSpeaking(true);
      fullTextRef.current = sentence;
      animateText(sentence);
    };

    utterance.onend = () => {
      setIsSpeaking(false);
      setShowButtons(true);
    };

    synth.speak(utterance);
  };

  const animateText = (sentence) => {
    setTextIndex(0);
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setTextIndex((prev) => {
        if (prev < sentence.length) {
          return prev + 1;
        } else {
          clearInterval(intervalRef.current);
          return prev;
        }
      });
    }, 100);
  };

  // 用户首次点击页面，才触发朗读
  useEffect(() => {
    const handleClick = () => {
      if (!hasStarted) {
        setHasStarted(true);
        speak(text);
        window.removeEventListener('click', handleClick);
      }
    };
    window.addEventListener('click', handleClick);
    return () => window.removeEventListener('click', handleClick);
  }, [hasStarted]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-white to-blue-50 text-center px-4 font-sans transition-all duration-500">
      {/* 圆环 */}
      <div
        className={`w-40 h-40 rounded-full border-4 flex items-center justify-center text-4xl mb-8 transition-all ${
          isSpeaking ? 'border-blue-500 animate-ping-slow' : 'border-gray-300'
        }`}
      >
        🤖
      </div>

      {/* 字幕逐字显示 */}
      <p className="text-lg text-gray-700 font-medium h-10">
        {fullTextRef.current.slice(0, textIndex)}
        {hasStarted && textIndex < text.length && (
          <span className="inline-block w-2 h-5 bg-gray-400 animate-pulse align-middle ml-1"></span>
        )}
      </p>

      {/* 引导点击提示 */}
      {!hasStarted && (
        <p className="text-sm text-blue-500 mt-4 animate-pulse">
          👋 请点击页面任意位置，开始体验
        </p>
      )}

      {/* 渐入按钮 */}
      <div
        className={`mt-10 flex gap-6 transition-opacity duration-1000 ${
          showButtons ? 'opacity-100' : 'opacity-0 pointer-events-none'
        }`}
      >
        <button className="bg-white/70 hover:bg-white/90 px-6 py-3 rounded-lg shadow text-blue-700 font-semibold backdrop-blur">
          找工作
        </button>
        <button className="bg-white/70 hover:bg-white/90 px-6 py-3 rounded-lg shadow text-green-700 font-semibold backdrop-blur">
          招聘
        </button>
      </div>

      {/* 自定义缓慢呼吸波动动画 */}
      <style>
        {`
          @keyframes ping-slow {
            0% { transform: scale(1); opacity: 1; }
            50% { transform: scale(1.2); opacity: 0.6; }
            100% { transform: scale(1); opacity: 1; }
          }
          .animate-ping-slow {
            animation: ping-slow 1.2s infinite;
          }
        `}
      </style>
    </div>
  );
}
