    const utterance = new SpeechSynthesisUtterance(sentence);
    utterance.lang = 'zh-CN';
    utterance.onstart = () => {
      setIsSpeaking(true);
      fullTextRef.current = sentence;
      animateText(sentence);
    };
    utterance.onend = () => {
      setIsSpeaking(false);
      setShowButtons(true);
    };
    speechSynthesis.speak(utterance);
  };

  // ÈÄêÂ≠óÂä®Áîª
  const animateText = (sentence) => {
    setTextIndex(0);
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setTextIndex((prev) => {
        if (prev < sentence.length) {
          return prev + 1;
        } else {
          clearInterval(intervalRef.current);
          return prev;
        }
      });
    }, 100); // ÊéßÂà∂ÊØè‰∏™Â≠óÁöÑÈó¥Èöî
  };

  useEffect(() => {
    speak(text);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-white to-blue-50 text-center px-4 font-sans transition-all duration-500">
      {/* ÂúÜÁéØÊ≥¢Âä® */}
      <div
        className={`w-40 h-40 rounded-full border-4 flex items-center justify-center text-4xl mb-8 transition-all ${
          isSpeaking ? 'border-blue-500 animate-ping-slow' : 'border-gray-300'
        }`}
      >
        ü§ñ
