import React, { useEffect, useRef, useState } from 'react';

export default function AIAgentPage() {
  const [textIndex, setTextIndex] = useState(0);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [showButtons, setShowButtons] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const text = '„Åì„Çì„Å´„Å°„ÅØ„ÄÅÁßÅ„ÅØ„Åä‰ªï‰∫ã„Éû„ÉÉ„ÉÅ„É≥„Ç∞„Ç¢„Ç∑„Çπ„Çø„É≥„Éà„Åß„Åô„ÄÇ„Åä‰ªï‰∫ã„ÅÆ„ÅäÊé¢„Åó„Åß„Åô„ÅãÔºü‰∫∫Êùê„ÅÆ„ÅäÊé¢„Åó„Åß„Åô„ÅãÔºü';
  const fullTextRef = useRef('');
  const intervalRef = useRef(null);

  const animateText = (sentence) => {
    fullTextRef.current = sentence; // Èü≥Â£∞„Çµ„Éñ„Çø„Ç§„Éà„É´
    setTextIndex(0);
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setTextIndex((prev) => {
        if (prev < sentence.length) {
          return prev + 1;
        } else {
          clearInterval(intervalRef.current);
          return prev;
        }
      });
    }, 100);
  };

  useEffect(() => {
    const handleClick = () => {
      if (!hasStarted) {
        setHasStarted(true);
        const audio = document.getElementById('welcomeAudio');
        if (audio) {
          audio.play();
          audio.onplay = () => setIsSpeaking(true);
          audio.onended = () => {
            setIsSpeaking(false);
            setShowButtons(true);
          };
        }
        animateText(text);
        window.removeEventListener('click', handleClick);
      }
    };

    window.addEventListener('click', handleClick);
    return () => window.removeEventListener('click', handleClick);
  }, [hasStarted]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-[#0f172a] to-[#1e293b] text-center px-4 font-sans text-white transition-all duration-500">
      {/* ÂúÜÁéØ + Ê∞¥Ê≥¢ÊïàÊûú */}
      <div
        className={`relative w-40 h-40 rounded-full border-4 flex items-center justify-center text-4xl mb-8 transition-all ${
          isSpeaking ? 'border-blue-400 animate-pulse-fast' : 'border-gray-500'
        }`}
      >
        <img src="/images/assistant.png" alt="AIÂ§¥ÂÉè" className="w-40 h-38 rounded-full z-10" />

        {isSpeaking && (
          <div className="absolute top-0 left-0 w-full h-full rounded-full border-4 border-blue-300 animate-ripple z-0"></div>
        )}
      </div>

      {/* Èü≥È¢ëÊí≠ÊîæÂÖÉÁ¥† */}
      <audio id="welcomeAudio" src="/voices/welcome.mp3" preload="auto" />

      {/* Â≠óÂπïÈÄêÂ≠óÊòæÁ§∫ */}
      <p className="text-lg font-medium h-10">
        {fullTextRef.current.slice(0, textIndex)}
        {hasStarted && textIndex < text.length && (
          <span className="inline-block w-2 h-5 bg-blue-400 animate-pulse align-middle ml-1"></span>
        )}
      </p>

      {/* ÂºïÂØºÊèêÁ§∫ */}
      {!hasStarted && (
        <p className="text-sm text-blue-300 mt-4 animate-pulse">
          üëã ËØ∑ÁÇπÂáªÈ°µÈù¢‰ªªÊÑè‰ΩçÁΩÆÔºåÂºÄÂßã‰ΩìÈ™å
        </p>
      )}

      {/* Ê∏êÂÖ•ÊåâÈíÆ */}
      <div
        className={`mt-10 flex gap-6 transition-opacity duration-1000 ${
          showButtons ? 'opacity-100' : 'opacity-0 pointer-events-none'
        }`}
      >
        <button className="bg-white/20 hover:bg-white/30 px-6 py-3 rounded-lg shadow text-blue-200 font-semibold backdrop-blur transition">
          ‰ªï‰∫ãÊé¢„Åó
        </button>
        <button className="bg-white/20 hover:bg-white/30 px-6 py-3 rounded-lg shadow text-green-200 font-semibold backdrop-blur transition">
          ‰∫∫ÊùêÊé¢„Åó
        </button>
      </div>

      {/* Ëá™ÂÆö‰πâÂä®Áîª */}
      <style>
        {`
          @keyframes pulse-fast {
            0% { transform: scale(1); opacity: 1; }
            50% { transform: scale(1.3); opacity: 0.5; }
            100% { transform: scale(1); opacity: 1; }
          }
          .animate-pulse-fast {
            animation: pulse-fast 0.5s infinite;
          }
          @keyframes ripple {
            0% { transform: scale(1); opacity: 0.5; }
            100% { transform: scale(2.5); opacity: 0; }
          }
          .animate-ripple {
            animation: ripple 1s infinite;
          }
        `}
      </style>
    </div>
  );
}
