import React, { useEffect, useRef, useState } from 'react';

export default function AIAgentPage() {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const circleRef = useRef(null);

  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'zh-CN';
    utterance.onstart = () => setIsSpeaking(true);
    utterance.onend = () => setIsSpeaking(false);
    speechSynthesis.speak(utterance);
  };

  useEffect(() => {
    const welcomeText = '你好，我是您的AI找工作助手，请问能帮您什么吗？';
    speak(welcomeText);
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-white to-blue-50 text-center px-4">
      {/* 动态圆环 */}
      <div
        ref={circleRef}
        className={`w-40 h-40 rounded-full border-4 border-blue-400 flex items-center justify-center text-4xl transition-all ${
          isSpeaking ? 'animate-pulse' : ''
        }`}
      >
        🤖
      </div>

      {/* 字幕文字 */}
      <p className="mt-8 text-lg text-gray-700 font-medium">
        你好，我是您的AI找工作助手，请问能帮您什么吗？
      </p>

      {/* 按钮区域 */}
      <div className="mt-10 flex gap-6">
        <button className="bg-white/70 hover:bg-white/90 px-6 py-3 rounded-lg shadow text-blue-700 font-semibold backdrop-blur">
          找工作
        </button>
        <button className="bg-white/70 hover:bg-white/90 px-6 py-3 rounded-lg shadow text-green-700 font-semibold backdrop-blur">
          招聘
        </button>
      </div>
    </div>
  );
}
